import React from "react";
import "./helper.css";
import { Component } from "react";

class LoginForm extends Component {
  state = { email: "", password: "" };

  constructor(props) {
    super(props);
    this.handleLogin = this.handleLogin.bind(this);
  }

  handleLogin(e) {
    this.props.login(
      e.target.elements.inputEmail.value,
      e.target.elements.inputPassword.value
    );
  }

  render() {
    return (
      <div className="position-relative m-4">
        <form>
          <div className="form-group-auto m-2">
            <label>E-mail</label>
            <input
              onChange={this.emailOnChange}
              type="email"
              className="form-control"
              name="inputEmail"
              placeholder="Insira aqui o seu e-mail"
            />
          </div>
          <div className="form-group-auto m-2">
            <label htmlFor="inputPassword">Password</label>
            <input
              onChange={this.passwordOnChange}
              type="password"
              className="form-control"
              name="inputPassword"
              placeholder="Insira aqui a sua password"
            />
          </div>
          <div className="form-check m-4">
            <input
              className="form-check-input"
              type="checkbox"
              value=""
              id="defaultCheck1"
            />
            <label className="form-check-label" htmlFor="defaultCheck1">
              Manter-me logado
            </label>
          </div>
          <button
            type="button"
            className="btn btn-primary"
            onClick={this.handleLogin}
          >
            {" "}
            Login
          </button>
        </form>
        <small>
          <label className="form-check-label" htmlFor="defaultCheck1">
            NÃ£o tem conta? Pode registar-se
            <a href="/regUser"> aqui</a>!
          </label>
        </small>
      </div>
    );
  }
}

export default LoginForm;
