import React, { Component } from "react";

class UserRegForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      secondPassword: "",
      username: "",
      current: 0
    };
    this.handleEmail = this.handleEmail.bind(this);
    this.handlePassword = this.handlePassword.bind(this);
    this.handleSecondPassword = this.handleSecondPassword.bind(this);
    this.handleUsername = this.handleUsername.bind(this);
  }

  handleEmail(event) {
    this.setState({ email: event.target.value });
  }

  handlePassword(event) {
    this.setState({ password: event.target.value });
  }

  handleSecondPassword(event) {
    this.setState({ secondpassword: event.target.value });
  }

  handleUsername(event) {
    console.log(this.state.username);
    this.setState({ username: event.target.value });
  }

  render() {
    return (
      <div className="position-relative m-4">
        <form>
          <div className="form-group">
            <label htmlFor="exampleInputUsername">Nome de Utilizador</label>
            <input
              onChange={this.handleUsername}
              type="username"
              className="form-control"
              id="exampleInputUsername"
            />
          </div>
          <div className="form-group">
            <label htmlFor="exampleInputEmail1">E-mail</label>
            <input
              onChange={this.handleUsername}
              type="email"
              className="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
            />
            <small id="emailHelp" className="form-text text-muted">
              Nunca partilharemos o seu e-mail com ningu√©m.
            </small>
          </div>
          <div className="form-group">
            <label htmlFor="InputPassword1">Password</label>
            <input
              type="password"
              className="form-control"
              id="InputPassword1"
            />
          </div>
          <div className="form-group">
            <label htmlFor="InputPassword2">
              Introduza novamente a Password
            </label>
            <input
              type="password"
              className="form-control"
              id="InputPassword2"
            />
          </div>
          <button type="Registar" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    );
  }
}

export default UserRegForm;
