import React from "react";
import "./helper.css";
import { Component } from "react";

class LoginForm extends Component {
  state = { email: "", password: "" };

  constructor(props) {
    super(props);
    this.emailOnChange = this.emailOnChange.bind(this);
    this.passwordOnChange = this.passwordOnChange.bind(this);
    this.inputEmail = React.createRef();
    this.inputPassword = React.createRef();
  }

  emailOnChange = text => {
    this.state.setState({ email: text });
  };

  passwordOnChange = text => {
    this.state.setState({ password: text });
  };

  render() {
    return (
      <div className="position-relative m-4">
        <form>
          <div className="form-group-auto m-2">
            <label>E-mail</label>
            <input
              ref={this.inputEmail}
              onChange={event => this.emailOnChange(event.target.value)}
              type="email"
              className="form-control"
              placeholder="Insira aqui o seu e-mail"
            />
          </div>
          <div className="form-group-auto m-2">
            <label htmlFor="inputPassword">Password</label>
            <input
              ref={this.inputPassword}
              onChange={this.passwordOnChange}
              type="password"
              className="form-control"
              placeholder="Insira aqui a sua password"
            />
          </div>
          <div className="form-check m-4">
            <input
              className="form-check-input"
              type="checkbox"
              value=""
              id="defaultCheck1"
            />
            <label className="form-check-label" htmlFor="defaultCheck1">
              Manter-me logado
            </label>
          </div>
          <button
            type="button"
            className="btn btn-primary"
            onClick={() =>
              this.props.login(this.state.email, this.state.password)
            }
          >
            {" "}
            Login
          </button>
        </form>
        <small>
          <label className="form-check-label" htmlFor="defaultCheck1">
            NÃ£o tem conta? Pode registar-se
            <a href="/regUser"> aqui</a>!
          </label>
        </small>
      </div>
    );
  }
}

export default LoginForm;
