import React, { Component } from "react";
import VisitComponent from "./VisitComponent";
import ReactList from "react-list";
import { getVisitas, getInstDepNames } from "../HTTPRequests";

class VisitHistory extends Component {
  constructor(props) {
    super(props);
    this.state = {
      visitas: [],
      names: [],
    };

    const urlParams = new URLSearchParams(window.location.search);
    const u = urlParams.get("u");
    var n = [];

    getVisitas(u).then((r) => {
      r.text().then((r) => {
        var json = JSON.parse(r);
        json.forEach((element) => {
          getInstDepNames(element.id_inst, element.departamentosID).then(
            (r) => {
              r.text().then((r) => {
                var json2 = JSON.parse(r);
                n.push(json2);
                this.setState({ names: n });
              });
            }
          );
        });
        this.setState({ visitas: json });
      });
    });

    this.renderItem = this.renderItem.bind(this);
  }

  renderItem(index, key) {
    return (
      <div key={key}>
        {
          <VisitComponent
            data_saida={this.state.visitas[index].data_saida}
            data_inicio={this.state.visitas[index].data_inicio}
            visitado={this.state.visitas[index].visitado}
            avaliacao={this.state.visitas[index].avaliacao}
            comentario={this.state.visitas[index].comentario}
            id_inst={this.state.names[0].id_inst}
            departamentosID={this.state.names[0].departamentosID}
          />
        }
      </div>
    );
  }

  render() {
    return (
      <ReactList
        itemRenderer={this.renderItem}
        length={this.state.visitas.length}
        type="uniform"
      />
    );
  }
}

export default VisitHistory;
