export async function login(email, password) {
  const url = "http://localhost:8080";

  fetch(url, {
    method: "POST",
    body: JSON.stringify({
      t: "login",
      email: email,
      password: password
    }),
    headers: new Headers()
  })
    .then(response => response.text())
    .then(response => {
      if (response === "true") {
        return true;
      } else {
        return false;
      }
    })
    .catch(err => console.log(err));
}

export async function createUser(state) {
  const url = "http://localhost:8080";

  fetch(url, {
    method: "POST",
    body: JSON.stringify({
      t: "create",
      email: state.email,
      password: state.password,
      username: state.username,
      phone: state.phone,
      morada: state.morada,
      postCode: state.postCode
    }),
    headers: new Headers()
  })
    .then(response => response.text())
    .then(response => {
      console.log(response);
    })
    .catch(err => console.log(err));
}
